import React from "react";
import { useSelector } from "react-redux";

const Search = () => {
  const storeSearchItems = useSelector((state) => state.topBar.searchItems);
  const searchResults = storeSearchItems.map((x, index) => {
    return (
      <div className="searchResults" key={index}>
        <a
          href={x.hasOwnProperty("trailer") === "undefined" ? x.url : x.trailer}
        >
          <img src={x.image_url} alt="" />
          <p className="searchTitle">{x.title}</p>
        </a>
      </div>
    );
  });
  return (
    <div className="searchContainer">
      {storeSearchItems.length < 1 ? (
        <h1>No results found. Try refining your search term.</h1>
      ) : (
        searchResults
      )}
    </div>
  );
};
export default Search;
